import { setupCli } from "./twistlock";
import { SetupCliReturn, TwistlockResults, TwistlockRun } from "./twistlock";
import { yarmWhyFormat } from "./yarnWhyFormat";
import { postGithubComment } from "./githubComment";
import * as core from "@actions/core";

const tag =
  '\n<hr><p align="right"><em>Generated by <a href="https://github.com/resideo/actions/tree/master/twistlock">resideo/actions/twistlock</a>.<br>Please <a href="https://github.com/resideo/actions/issues/new">create an issue</a> in the repository if you have any feedback.<em></p><!-- Twistlock Action by Frontside Software -->';

export function* run({
  user,
  password,
  token,
  consoleUrl,
  project,
  repositoryPath,
  image,
  octokit,
}: TwistlockRun) {
  const twistcli: SetupCliReturn = yield setupCli({
    user,
    password,
    token,
    consoleUrl,
    project,
  });

  console.log(`running in ${repositoryPath}`);
  const { results, code }: { results: TwistlockResults; code: number } =
    yield twistcli.scanRepository({
      repositoryPath,
      image,
    });

  const { message, workflowStatus } = yield yarmWhyFormat({
    message: results,
    tag,
    repositoryPath,
  });

  console.log("::group::comment");
  console.dir(message);
  console.log("::endgroup::");
  yield postGithubComment(octokit, { message, tag });
  if (workflowStatus !== "pass")
    core.setFailed("One or more packages have an overdue security resolution.");
  if (code !== 0)
    core.setFailed(
      `CLI exited with code ${code}. This implies there may be compliance issues.`
    );
}
